package com.example.manager.ui.onboarding.adapter

import android.view.LayoutInflater
import android.view.ViewGroup
import androidx.core.view.isVisible
import androidx.recyclerview.widget.RecyclerView.Adapter
import androidx.recyclerview.widget.RecyclerView.ViewHolder
import com.example.manager.databinding.ItemOnboardingBinding
import com.example.manager.model.OnBoarding
import com.example.manager.utills.loadImage

class OnBoardingAdapter(private val onClick: () -> Unit) :
    Adapter<OnBoardingAdapter.OnBoardingViewHolder>() {

    val data = arrayListOf(
        OnBoarding("data:image/jpeg;base64,", "Исход любой битвы", "невозможно определить заранее"),
        OnBoarding("data:image/jpeg;base64,", "Я отказываюсь", "от твоего отказа"),
        OnBoarding("https://c1.klipartz.com/pngpicture/629/207/sticker-png-book-delivery-freight-transport-truck-package-delivery-logistics-line-vehicle.png", "Здесь могла быть", "ваша реклама"),
    )

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): OnBoardingViewHolder {
        return OnBoardingViewHolder(
            ItemOnboardingBinding.inflate(
                LayoutInflater.from(parent.context),
                parent,
                false
            )
        )
    }

    override fun onBindViewHolder(holder: OnBoardingViewHolder, position: Int) {
        holder.bind(data[position])
    }

    override fun getItemCount(): Int {
        return data.size
    }

    inner class OnBoardingViewHolder(private val binding: ItemOnboardingBinding) :
        ViewHolder(binding.root) {
        fun bind(onBoarding: OnBoarding) {
            binding.imgOnboarding.loadImage(onBoarding.image)

            binding.btnStart.setOnClickListener {
                onClick()
            }
            binding.btnSkip.setOnClickListener {
                onClick()
            }

            binding.btnStart.isVisible = adapterPosition == data.lastIndex
            binding.btnSkip.isVisible = adapterPosition != data.lastIndex

            binding.tvTitle.text = onBoarding.title
            binding.tvDesc.text = onBoarding.desc
        }
    }

}